import './Products.css';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Link } from 'react-router-dom';

const Card = (props) => (
  <div className="products__card">
    <span className="products__card-eyebrow">{props.eyebrow}</span>
    <h3 className="products__card-title">{props.title}</h3>
    <button className="button">{props.cta}</button>
    <p className="products__card-body">{props.body}</p>
    <Link>Learn more</Link>
  </div>
);

function Products() {
  return (
    <div className="products__bg">


      <Container>
        <Row>
          <Col xs={8}>
            <h3 className="heading3 white">Our products eliminate the need for third parties, whether traditional banks or crypto exchanges, and give individuals full control over their assets.</h3>
          </Col>
        </Row>
        <Row>
          <Col xs={6}>
            <Card
              eyebrow='Seamless'
              title='Permissionless, unstoppable cross-blockchain trading'
              cta='Launching 2022'
              body='Use Bitcoin across blockchains—without giving up control of your keys.'
            />
          </Col>
          <Col xs={6}>
            <Card
              eyebrow='MoneyClip'
              title='Simplified access to the world of DeFi.'
              cta='Launching Fall 2021'
              body='Link your bank to blockchain and enjoy regulated access to the world’s best DeFi options.'
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Products;
