import './BlogCard.css';
import { Link } from 'react-router-dom';
import arrow from '../../../images/icons/arrow-up-right.svg';

const BlogCardBody = ({ post }) => (
  <div className="blogCard__container">
    <div className="text-right">
      <img src={arrow} style={{ width: 22 }} alt="Arrow - launch" />
    </div>

    <h3 className="header4 mb-0" style={{ marginRight: 25 }}>{post.title}</h3>
    <p className="header5 blogCard__body" >{post.summary}</p>

    <div className="text-md-right">
      <label className="eyebrow blogCard__footnote">{post.newsSource ? post.newsSource.toUpperCase() : 'ERTHOS BLOG'}</label>
    </div>
  </div>
)

function BlogCard(props) {
  const { post } = props;

  return (
    <>
      {post.externalLink ?
        <a className="blogCard__link" href={post.externalLink}>
          <BlogCardBody post={post} />
        </a>
        :
        <Link to={{ pathname: `/article/${post.order}`, state: { post } }} className="blogCard__link">
          <BlogCardBody post={post} />
        </Link>
      }
    </>
  );
}

export default BlogCard;
