import './NavBarDesktop.css'
import logoWhite from '../../../../images/logos/wellfield-white.svg';
import logoBlack from '../../../../images/logos/wellfield-black.svg';

import { useState, useRef } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

function NavBarDesktop({ config }) {
  const buttonRef = useRef();
  const history = useHistory();
  let { pathname } = useLocation();
  const [show, setShow] = useState(false);
  const solutionsPaths = ['/solutions/inputs', '/solutions/end-products'];

  const showDropdown = () => {
    setShow(!show);
  }
  const hideDropdown = () => {
    setShow(false);
  }
  const onNav = (path) => {
    history.push(path)
    setShow(false)
  }
  const handleContactClick = () => {
    history.push('/contact')
    buttonRef.current.blur();
  }

  return (
    <Navbar expand="md" collapseOnSelect>
      <Container>
        <Navbar.Brand
          style={{ cursor: 'pointer' }}
          onClick={() => history.push('/')}><img src={logoBlack} alt="Eco-Packers logo" />
        </Navbar.Brand>
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <div onMouseEnter={showDropdown} onMouseLeave={hideDropdown}>
              <div onClick={() => onNav('/solutions/inputs')}
                className={`navBar__link ${solutionsPaths.includes(pathname) ? 'navBar__link--active' : ''}`}
              >Solutions</div>
              {
                show ?
                  <div className="navBar__dropdown">
                    <span onClick={() => onNav('/solutions/inputs')} className="navBar__dropdown-item">Inputs</span>
                    <span onClick={() => onNav('/solutions/end-products')} className="navBar__dropdown-item" style={{ marginTop: 10 }}>End Products</span>
                    {config.isFaqEnabled &&
                      <span onClick={() => onNav('/faq')} className="navBar__dropdown-item" style={{ marginTop: 10 }}>FAQ</span>
                    }
                  </div>
                  : ''
              }
            </div>

            <div className={`navBar__link ${pathname.includes('/about') ? 'navBar__link--active' : ''}`}
              onClick={() => history.push('/about')}
            >About</div>

            <button onClick={() => handleContactClick()} ref={buttonRef} className="button navBar__button">Contact Us</button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar >
  );
}

export default NavBarDesktop;
