import './Future.css';
import avatar from '../../../images/people/avatar.png';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Card = (props) => (
  <div className={`future__card products__card--${props.type}`}>
    <img src={props.image} />
    <h3 className="future__card-title heading2">{props.title}</h3>
    <p className="future__card-body p1">{props.body}</p>
  </div>
);

function Future() {
  return (
    <>
      <div className="future__bg">
        <Container>
          <Row>
            <Col xs={12}><h2 className="investors__heading heading1">How does Seamless contribute to the future of finance?</h2></Col>
          </Row>

          <Row>
            <Col xs={12} lg={6}>
              <Card
                image='Seamless'
                title='Permissionless, unstoppable cross-blockchain trading'
                body='Use Bitcoin across blockchains—without giving up control of your keys.'
                type='left'
              />
            </Col>
            <Col xs={12} lg={6}>
              <Card
                image='MoneyClip'
                title='Simplified access to the world of DeFi.'
                body='Link your bank to blockchain and enjoy regulated access to the world’s best DeFi options.'
                type='right'
              />
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}

export default Future;
