import './Certifications.css';
import { getCertifications } from '../../../contentful';

import { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const promise = getCertifications();

function Certifications() {
  const [certifications, setCertifications] = useState([]);

  useEffect(() => {
    promise.then(certs => {
      setCertifications(certs);
    });
  }, []);

  return (
    <Container className="certifications__container">
      <div style={{ maxWidth: 837, margin: 'auto' }}>
        <Row>
          <Col xs={12}>
            <label className="eyebrow primary-rust">INDUSTRY CERTIFICATIONS</label>
            <h2 className="header2 certifications__header">Accepted and recognized by the highest global standards</h2>
          </Col>
        </Row>
        <Row>
          <Col xs={12}>
            <div className="d-md-flex justify-content-center">
              {certifications.map((cert, index) => (
                <div key={index} style={{ margin: 20 }}>
                  <img src={cert.certificationLogo.fields.file.url} alt="Certification" />
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </div>
    </Container>
  );
}

export default Certifications;
