import './Join.css';
import rightBlack from '../../../images/icons/chevron-right-black.png';

import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Card = (props) => (
  <div className={`join__card join__card--${props.type}`}>
    <span className="join__card-number">{props.number}</span>

    <div className="join__button-wrapper">
      <button className={`button-lg join__button--${props.type}`}>{props.cta}</button>
    </div>
  </div>
);

function Join() {
  return (
    <div className="join__bg">
      <Container>
        <Row>
          <Col xs={12} md={6}>
            <Card
              number='5'
              cta='PhDs'
              type='top'
            />
            <Card
              number='15'
              cta='Engineers'
              type='bottom'
            />
          </Col>
          <Col xs={12} md={6}>
            <h2 className="join__heading heading2">Join Us</h2>
            <p className="p1">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Neque vitae pulvinar ullamcorper arcu cursus urna. Sit neque, ultrices euismod etiam. Ultrices eu nec porta fermentum et. Sit nulla cursus fringilla erat at facilisis.</p>
            <button className="button-lg join__button">Submit</button>
          </Col>

        </Row>
      </Container>
    </div>
  );
}

export default Join;
