import './NavBarDesktop.css'
import logoWhite from '../../../../images/logos/wellfield-white.svg';
import logoBlack from '../../../../images/logos/wellfield-black.svg';
import placeholder from '../../../../images/icons/Placeholder.svg';

import { useState, useRef } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

function NavBarDesktop({ config }) {
  const buttonRef = useRef();
  const history = useHistory();
  let { pathname } = useLocation();
  const [show, setShow] = useState(false);
  const solutionsPaths = ['/solutions/inputs', '/solutions/end-products'];

  const showDropdown = () => {
    setShow(!show);
  }
  const hideDropdown = () => {
    setShow(false);
  }
  const onNav = (path) => {
    history.push(path)
    setShow(false)
  }
  const handleContactClick = () => {
    history.push('/contact')
    buttonRef.current.blur();
  }

  return (
    <Navbar expand="md" collapseOnSelect>
      <Container>
        <Navbar.Brand
          style={{ cursor: 'pointer' }}
          onClick={() => history.push('/')}><img src={logoBlack} alt="Eco-Packers logo" />
        </Navbar.Brand>
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <div onMouseEnter={showDropdown} onMouseLeave={hideDropdown}>
              <div onClick={() => onNav('/seamless')}
                className={`navBar__link ${solutionsPaths.includes(pathname) ? 'navBar__link--active' : ''}`}
              >Our Brands</div>
              {
                show ?
                  <div className="navBar__dropdown">
                    <div onClick={() => onNav('/seamless')}>
                      <img src={placeholder} alt="Seamless logo" />
                      <span className="navBar__dropdown-title">Seamless</span>
                      <span className="navBar__dropdown-description">Permissionless, unstoppable cross-blockchain trading</span>
                    </div>
                    <div onClick={() => onNav('/moneyclip')} style={{ marginTop: 10 }}>
                      <img src={placeholder} alt="Moneyclip logo" />
                      <span className="navBar__dropdown-title">MoneyClip</span>
                      <span className="navBar__dropdown-description">Simplified access to the world of DeFi</span>
                    </div>


                  </div>
                  : ''
              }
            </div>

            <div className={`navBar__link ${pathname.includes('/about') ? 'navBar__link--active' : ''}`}
              onClick={() => history.push('/about')}
            >About Us</div>

            <button onClick={() => handleContactClick()} ref={buttonRef} className="button navBar__button">Contact Us</button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar >
  );
}

export default NavBarDesktop;
