import './MailingList.css';
import wfld from '../../../images/logos/wfld.svg';
import gear from '../../../images/logos/gear.svg';

import { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const encode = (data) => {
  return Object.keys(data)
    .map(key => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
    .join("&");
}

function MailingList() {
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');
  const [isSent, setIsSent] = useState(false);

  const handleSubmit = e => {

    let emailErrorMsg = '';
    if (email === '') {
      emailErrorMsg = 'Email required';
    } else if (!email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      emailErrorMsg = 'Please enter a valid email';
    }

    setEmailError(emailErrorMsg);

    if (emailErrorMsg === '') {
      fetch("/", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: encode({ "form-name": "contactUs", ...this.state })
      })
        .then(() => setIsSent(true))
        .catch(error => alert(error));
    }

    // this.submitBtn.current.blur();
    e.preventDefault();
  };

  return (
    <div className="mailing__bg">
      <Container>
        <Row>
          <Col xs={12} className="text-center">
            <img src={wfld} />
            <h2 className="mailing__heading heading2">Get investor updates.</h2>

            <form onSubmit={handleSubmit} autoComplete="off">
              <input type="hidden" name="form-name" value="contactUs" />
              <input
                className={`form__input-text ${this.state.formErrors.email ? 'form__error-input' : ''}`}
                type="email"
                name="email"
                value={email}
                onChange={this.handleChange}
                onFocus={this.handleFocus}
                onBlur={this.handleBlur}
                ref={this.emailInput}
                autoComplete="random"
              />
              {
                emailError ? <p className="form__error-msg">{this.state.formErrors.email}</p> : ''
              }
            </form>
            <button className="button-lg mailing__button">Submit</button>
            <img src={gear} />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default MailingList;
