import './Media.css';
import chevronLeft from '../../images/icons/chevron-left.svg';
import chevronRight from '../../images/icons/chevron-right.svg';
import Header from '../Shared/Header/Header';
import BlogCard from './BlogCard/BlogCard';
import ContactBanner from '../Shared/ContactBanner/ContactBanner';


import { Helmet } from 'react-helmet';
import { useEffect, useState, useRef } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';



function Media() {
  const [postsPerPageConfig, setPostsPerPageConfig] = useState(0);
  const [totalPosts, setTotalPosts] = useState(0);
  const [currentPosts, setCurrentPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [bubbles, setBubbles] = useState([]);
  const mediaMarker = useRef();


  const handleBubbleClick = (index) => {
    mediaMarker.current.scrollIntoView();
    setCurrentPage(index);
  }

  useEffect(() => {
    if (postsPerPageConfig) {
      getPosts(currentPage * postsPerPageConfig, postsPerPageConfig).then(posts => {
        setCurrentPosts(posts.data);
        setTotalPosts(posts.count);

        const array = [];
        const pageCount = postsPerPageConfig ? Math.ceil(posts.count / postsPerPageConfig) : 0;
        for (let i = 0; i < pageCount; i++) {
          array.push(
            <div key={i} onClick={() => handleBubbleClick(i)}
              className={`${i === currentPage ? 'page__pagination--active' : 'page__pagination--inactive'}`}>
              <span className={`eyebrow ${i === currentPage ? 'primary-neutral' : 'primary-green'}`} style={{ marginLeft: 2 }}>{i + 1}</span>
            </div>
          )
        }
        setBubbles(array);
      });
    }
  }, [postsPerPageConfig, currentPage]);

  const handleClick = (direction) => {
    mediaMarker.current.scrollIntoView();
    if (direction === 'previous' && currentPage !== 0) {
      setCurrentPage(currentPage - 1);
    }
    if (direction === 'next' && currentPage < (Math.ceil(totalPosts / postsPerPageConfig) - 1)) {
      setCurrentPage(currentPage + 1);
    }
  }

  return (
    <>
      <Helmet>
        <title>erthos&trade; - Media</title>
        <meta name="description"
          content="Single use plastics made with erthos solutions are 100% compostable, and compatible with existing plastic technology, making plant powered plastics the natural choice." />
        <meta property="og:title" content="erthos&trade; - Media" />
        <meta property="og:description" content="Plant powered alternatives to traditional plastics" />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://www.erthos.ca/media" />
        <meta property="og:image" content="https://www.erthos.ca/leaf-home-mobile.png" />
      </Helmet>

      <Header
        title="The erthos&trade; Media Platform"
        subtitle="Keep up-to-date with our most recent media coverage, sustainability articles and key industry news. "
        blob="rust"
      />

      <div className="media__background">
        <Container>
          <div className="media__blog">
            <Row>
              <Col xs={12}>
                <div className="text-center text-md-left" ref={mediaMarker}>
                  <label className="eyebrow primary-rust mb-0">INDUSTRY TOPICS & NEWS</label>
                  <h2 className="header2 media__header">Latest News</h2>
                </div>
                {currentPosts.map((post, index) =>
                  <BlogCard key={index} post={post} />
                )}
                <div className="page__pagination-container">
                  <img src={chevronLeft} style={{ marginRight: 5, cursor: 'pointer' }} onClick={() => handleClick('previous')} alt="Left chevron icon" />
                  {bubbles}
                  <img src={chevronRight} style={{ marginLeft: 5, cursor: 'pointer' }} onClick={() => handleClick('next')} alt="Right chevron icon" />
                </div>

              </Col>
            </Row>
          </div>
        </Container>
      </div>

      <ContactBanner background="green" eyebrow="GET IN TOUCH" header="Learn more about the work we do at erthos&trade;" />
    </>
  );
}

export default Media;
