import './Article.css';
import arrow from '../../images/icons/arrow-left-green.svg';
import rust from '../../images/blobs/blob-rust.svg';
import ErrorPage from '../404/404';
import { getPost } from '../../contentful';

import { Helmet } from 'react-helmet';
import { Component } from 'react';
import { Link } from 'react-router-dom';
import { BLOCKS, INLINES, MARKS } from '@contentful/rich-text-types';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const options = {
  renderMark: {
    [MARKS.BOLD]: (text) => <span className="contentful--bold">{text}</span>,
    [MARKS.ITALIC]: (text) => <span className="contentful--italic">{text}</span>,
    [MARKS.UNDERLINE]: (text) => <span className="contentful--underline">{text}</span>,
  },
  renderNode: {
    [BLOCKS.HEADING_1]: (node, children) => <h1 className="header1">{children}</h1>,
    [BLOCKS.HEADING_2]: (node, children) => <h2 className="header2">{children}</h2>,
    [BLOCKS.HEADING_3]: (node, children) => <h3 className="header3">{children}</h3>,
    [BLOCKS.HEADING_4]: (node, children) => <h4 className="header4">{children}</h4>,
    [BLOCKS.HEADING_5]: (node, children) => <h5 className="header5">{children}</h5>,
    [BLOCKS.HEADING_6]: (node, children) => <h6 className="eyebrow">{children}</h6>,
    [BLOCKS.QUOTE]: (node, children) => <blockquote className="contentful__blockquote">{children}</blockquote>,
    [BLOCKS.PARAGRAPH]: (node, children) => <p className="paragraph">{children}</p>,
    [BLOCKS.EMBEDDED_ASSET]: (node, children) => <div className="article__body-image">
      <img key={node.data.target.fields.file.url}
        src={node.data.target.fields.file.url}
        alt="Blog post"
      />
    </div>,
    [BLOCKS.UL_LIST]: (node, children) => <ul>{children}</ul>,
    [BLOCKS.OL_LIST]: (node, children) => <ol>{children}</ol>,
    [BLOCKS.LIST_ITEM]: (node, children) => <li>{children}</li>,
    [INLINES.HYPERLINK]: (node, children) => <a className="link-normal" href={node.data.uri}>{children}</a>
  },
};

export default class BlogPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      post: {},
      error: false
    }
  }

  async componentDidMount() {
    const post = this.props.location.state?.post;
    if (post) {
      post.article = documentToReactComponents(post.article, options);
      this.setState({ post });
    } else {
      const posts = await getPost(this.props.match.params.id);
      if (posts.length) {
        if (posts[0].externalLink) {
          this.setState({ error: true });
        } else {
          posts[0].article = documentToReactComponents(posts[0].article, options);
          this.setState({ post: posts[0] });
        }
      } else {
        this.setState({ error: true });
      }
    }
  }

  render() {
    const { post, error } = this.state;
    if (error) {
      return <ErrorPage />
    }
    return (
      <>
        <Helmet>
          <title>erthos&trade; - Blog Article</title>
          <meta name="description"
            content="Single use plastics made with erthos solutions are 100% compostable, and compatible with existing plastic technology, making plant powered plastics the natural choice." />
          <meta property="og:title" content="erthos&trade; - Blog Article" />
          <meta property="og:description" content="Plant powered alternatives to traditional plastics" />
          <meta property="og:type" content="website" />
          <meta property="og:url" content="https://www.erthos.ca/article/" />
          <meta property="og:image" content="https://www.erthos.ca/leaf-home-mobile.png" />
        </Helmet>

        <Container>
          <Row>
            <Col xs={12}>
              <div className="text-right" style={{ maxWidth: 1061, margin: 'auto' }}>
                <img className="article__blob" src={rust} alt="Red circle" />
              </div>

              <div className="text-center" style={{ maxWidth: 830, margin: 'auto' }}>
                <p className="eyebrow primary-green mb-0">{post.date}</p>
                <h1 className="header2 article__header">{post.title}</h1>
                <p className="header5 primary-green mb-0">{post.summary}</p>
              </div>
            </Col>
          </Row>
        </Container>

        <Container className="d-none d-md-block">
          <Row>
            <Col xs={12}>
              <div className="article__header-image">
                <img src={post.image?.fields?.file?.url} alt="Article hero" />
              </div>
            </Col>
          </Row>
        </Container>
        <div className="article__header-image d-md-none">
          <img src={post.image?.fields?.file?.url} alt="Article hero" />
        </div>

        <Container>
          <Row>
            <Col xs={12}>
              <div style={{ maxWidth: 840, margin: 'auto' }}>
                {post.article}
              </div>

              <div className="article__footer">
                <label className="eyebrow primary-rust">READ MORE</label>
                <div className="d-flex justify-content-between">
                  <h2 className="header2">View all media</h2>
                  <Link to="/media">
                    <img src={arrow} style={{ width: 69 }} alt="Arrow back" />
                  </Link>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </>
    )
  };
};
