import './Solutions.css';
import logoBlobDesktop from '../../images/logos/logo-blob-desktop.svg';
import logoMobile from '../../images/logos/logo-blob-mobile.svg';
import cleanTech from '../../images/ticker/clean-tech.svg';
import compostable from '../../images/ticker/compostable.svg';
import guiltFree from '../../images/ticker/guilt-free.svg';
import manufacturing from '../../images/ticker/manufacturing.svg';
import planet from '../../images/ticker/planet.svg';
import plasticFree from '../../images/ticker/plastic-free.svg';
import treeFree from '../../images/ticker/tree-free.svg';
import wasteFree from '../../images/ticker/waste-free.svg';
import chevronDown from '../../images/icons/chevron-down-white.svg';
import Header from '../Shared/Header/Header';
import ContactBanner from '../Shared/ContactBanner/ContactBanner';
import Inputs from './Inputs/Inputs';
import EndProducts from './EndProducts/EndProducts';

import { useState, useRef, useEffect } from 'react';
import {
  Link,
  Switch,
  Route,
  useRouteMatch,
  useLocation,
} from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const IconContainerDesktop = () => (
  <div style={{ marginTop: 48 }}>
    <h2 className="header2 mb-0" style={{ maxWidth: 620 }}>A new standard for a better planet, with better materials</h2>
    <p className="header5 solutions-header-paragraph" style={{ maxWidth: 688 }}>We’re introducing a new standard in the industry. Everyday, single-use plastic solutions that are designed for the right quality, for the right amount of time.</p>
    <div className="d-flex flex-wrap" style={{ maxWidth: 742 }}>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={planet} alt="Planet icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">PLANET SAFE</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={cleanTech} alt="Light bulb icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">CLEAN TECH</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={plasticFree} alt="Plastic bag icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">ZERO PLASTIC</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={wasteFree} alt="Garbage can icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">WASTE FREE</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={guiltFree} alt="Heart icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">GUILT FREE</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={treeFree} alt="Tree icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">TREE FREE</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={manufacturing} alt="Mechanical arm icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">COMPATIBLE MANUFACTURING</span>
        </div>
      </div>
      <div className="solutions-icon-container">
        <img style={{ marginRight: 5, maxWidth: 'none' }} src={compostable} alt="Recycle icon" />
        <div style={{ display: 'table-cell', verticalAlign: 'middle' }}>
          <span className="eyebrow primary-green">HOME COMPOSTABLE</span>
        </div>
      </div>
    </div>
  </div>
);

const IconContainerMobile = () => (
  <Container className="d-md-none text-center" style={{ marginTop: 56, marginBottom: 81 }}>
    <Row>
      <Col xs={12}>
        <img src={logoMobile} className="solutions__image-mobile d-md-none" alt="Eco-Packers logo" style={{ marginBottom: 22 }} />
        <h2 className="header2 mb-0">A new standard for a better planet, with better materials</h2>
        <p className="header5 solutions-header-paragraph">We’re introducing a new standard in the industry. Everyday, single-use plastic solutions that are designed for the right quality, for the right amount of time.</p>
      </Col>
    </Row>
    <Row>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={planet} alt="Planet icon" />
        <span className="d-block eyebrow primary-green">PLANET SAFE</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={cleanTech} alt="Light bulb icon" />
        <span className="d-block eyebrow primary-green">CLEAN TECH</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={plasticFree} alt="Plastic bag icon" />
        <span className="d-block eyebrow primary-green">ZERO PLASTIC</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={wasteFree} alt="Garbage can icon" />
        <span className="d-block eyebrow primary-green">WASTE FREE</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={guiltFree} alt="Heart icon" />
        <span className="d-block eyebrow primary-green">GUILT FREE</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={treeFree} alt="Tree icon" />
        <span className="d-block eyebrow primary-green">TREE FREE</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={manufacturing} alt="Mechanical arm icon" />
        <span className="d-block eyebrow primary-green">COMPATIBLE MANUFACTURING</span>
      </Col>
      <Col xs={6}>
        <img style={{ marginRight: 5 }} src={compostable} alt="Recycle icon" />
        <span className="d-block eyebrow primary-green">HOME COMPOSTABLE</span>
      </Col>
    </Row>
  </Container>
);

function Solutions() {
  let { url, path } = useRouteMatch();
  let { pathname } = useLocation();
  const [isOpen, setIsOpen] = useState(false);
  const [position, setPosition] = useState('static');
  const [childOffset, setChildOffset] = useState(90);
  const stickyNavMobileMarker = useRef();
  const stickyNavDesktopMarker = useRef();
  const partnerWithUsMarker = useRef();

  useEffect(() => {
    const handleScroll = () => {
      if (window.matchMedia('(max-width: 767.98px)').matches) {
        if (window.scrollY > stickyNavMobileMarker?.current?.offsetTop && window.scrollY < (partnerWithUsMarker?.current?.offsetTop - 70)) {
          setPosition('fixed');
          setChildOffset(160);
        } else {
          setPosition('static');
          setChildOffset(90);
        }
      } else {
        if (window.scrollY > stickyNavDesktopMarker?.current?.offsetTop) {
          setPosition('fixed');
          setChildOffset(180);
        } else {
          setPosition('static');
          setChildOffset(90);
        }
      }

    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, []);

  const onNavClick = (screen) => {
    if (screen === 'mobile') {
      window.scroll(0, stickyNavMobileMarker.current.offsetTop);
    } else {
      window.scroll(0, stickyNavDesktopMarker.current.offsetTop);
    }
  }

  return (
    <>
      <Header
        title="Putting our Earth first"
        subtitle="Not everything needs to last forever"
        blob={window.matchMedia('(max-width: 767.98px)').matches ? "yellow" : "beige"}
      />


      {/* A new standard */}
      <div className="solutions__container--desktop d-none d-md-flex">
        <div style={{ marginRight: 30 }}>
          <img src={logoBlobDesktop} className="solutions__image--desktop" alt="Eco-Packers logo" />
        </div>
        <IconContainerDesktop />
      </div>
      <IconContainerMobile />

      {/* Nav banner - desktop */}
      <div ref={stickyNavDesktopMarker}></div>
      <div style={{ position: position }} className="solutions__sticky-header">
        <div className="solutions__banner--desktop d-none d-md-block" style={{ position: position }} >
          <Container>
            <Row>
              <Col md={6} >
                <h3 className="header4 mb-0">What are you looking for?</h3>
              </Col>
              <Col md={6} className="d-flex justify-content-end">
                <h3 className="header4 mb-0">
                  <Link to={`${url}/inputs`} onClick={() => onNavClick('desktop')}
                    className={`solutions__link--desktop ${pathname === '/solutions/inputs' ? 'primary-green' : 'primary-charcoal'}`}>Inputs</Link>
                </h3>
                <h3 className="header4 mb-0" style={{ marginLeft: 50 }}>
                  <Link to={`${url}/end-products`} onClick={() => onNavClick('desktop')}
                    className={`solutions__link--desktop ${pathname === '/solutions/end-products' ? 'primary-green' : 'primary-charcoal'}`}>End Products</Link>
                </h3>
              </Col>
            </Row>
          </Container>
        </div>
      </div>

      {/* Nav banner - mobile */}
      <div ref={stickyNavMobileMarker}></div>
      <div style={{ position: position }} className="solutions__sticky-header">
        <div className="solutions__banner--mobile d-md-none" onClick={() => setIsOpen(!isOpen)}>
          <Container>
            <Row>
              <Col xs={12} className="d-flex justify-content-between">
                <h3 className="header4 primary-neutral mb-0">What are you looking for?</h3>
                <img src={chevronDown} className={`${isOpen ? 'rotateimg180' : ''}`} alt={isOpen ? 'Arrow up' : 'Arrow down'} />
              </Col>
            </Row>
          </Container>
        </div>
        {
          isOpen &&
          <>
            <div className="solutions__banner-dropdown--mobile d-md-none">
              <Container>
                <Row>
                  <Col xs={12} className="d-flex justify-content-between">
                    <h3 className="header4 mb-0 ml-3">
                      <Link to={`${url}/inputs`} onClick={() => onNavClick('mobile')}
                        className={`solutions__link--mobile ${pathname === '/solutions/inputs' ? '' : 'solutions__link-opaque'}`}>Inputs</Link>
                    </h3>
                  </Col>
                </Row>
              </Container>
            </div>
            <div className="solutions__banner-dropdown--mobile d-md-none">
              <Container>
                <Row>
                  <Col xs={12} className="d-flex justify-content-between">
                    <h3 className="header4 mb-0 ml-3">
                      <Link to={`${url}/end-products`} onClick={() => onNavClick('mobile')}
                        className={`solutions__link--mobile ${pathname === '/solutions/end-products' ? '' : 'solutions__link-opaque'}`}>End Products</Link>
                    </h3>
                  </Col>
                </Row>
              </Container>
            </div>
          </>
        }
      </div>

      <Switch>
        <Route path={`${path}/inputs`} render={() => <Inputs offset={childOffset} />} />
        <Route path={`${path}/end-products`} render={() => <EndProducts offset={childOffset} />} />
      </Switch>

      <ContactBanner background="neutral" />
      <div ref={partnerWithUsMarker}></div>
    </>
  );
}

export default Solutions;
