import './Part.css';
import moneyclipLineAnimation from '../../../lotties/moneyclip/underlines/moneyclip-line.json';
import moneyclipStackAnimation from '../../../lotties/moneyclip/full-images/moneyclip-stack.json';

import lottie from 'lottie-web';
import { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Part() {
  const [playAnimation, setPlayAnimation] = useState(false); // same as slideIn
  const [stackAnimation, setStackAnimation] = useState(null);
  const animationRef = useRef(null);

  useEffect(() => {
    const topPosition = animationRef.current.getBoundingClientRect().top;
    const onScroll = () => {
      const scrollPosition = window.scrollY + window.innerHeight
      if (topPosition < scrollPosition) {
        setPlayAnimation(true);
      }
    }
    window.addEventListener('scroll', onScroll)
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  useEffect(() => {
    if (!document.getElementById('mc-part__stack-animation').hasChildNodes()) {
      const stack = lottie.loadAnimation({
        container: document.querySelector("#mc-part__stack-animation"),
        animationData: moneyclipStackAnimation,
        loop: false,
        autoplay: false,
        rendererSettings: { preserveAspectRatio: 'xMidYMid slice' }
      });
      setStackAnimation(stack);
    }
    if (stackAnimation && playAnimation) {

      stackAnimation.play();
    }
  }, [playAnimation]);

  return (
    <div className="mc-part__bg">
      <Container>
        <Row>
          <Col xs={12}>
            <div className="mc-part__card">
              <div>
                <h2 className="mc-part__heading heading1">Moneyclip is part of Wellfield.</h2>
                <p className="mc-part__body p1">MoneyClip directly monetizes end users through various financial products and services offered in-app.</p>
                <div ref={animationRef}></div>
                <div id="mc-part__stack-animation" />
              </div>
            </div>
          </Col>

        </Row>
      </Container>
    </div>
  );
}

export default Part;
