import './Timeline.css';
import checkmark from '../../../images/icons/checkmark.svg';
import timelineScroll from '../../../images/icons/timeline-scroll.svg';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5,
    partialVisibilityGutter: 30
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 4,
    partialVisibilityGutter: 30
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    partialVisibilityGutter: 30
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    partialVisibilityGutter: 30
  }
};

const CustomRightArrow = ({ onClick, ...rest }) => {
  const {
    onMove,
    carouselState: { currentSlide, deviceType }
  } = rest;
  // onMove means if dragging or swiping in progress.
  // return <div><img src={timelineScroll} onClick={() => onClick()} /></div>;
  return <button onClick={() => onClick()} />;
};

const ButtonGroup = ({ next, previous, goToSlide, ...rest }) => {
  const { carouselState: { currentSlide } } = rest;
  return (
    <div className="carousel-button-group"> // remember to give it position:absolute
      <button className={currentSlide === 0 ? 'disable' : ''} onClick={() => previous()} />
      <button onClick={() => next()} />
      <button onClick={() => goToSlide(currentSlide + 1)}> Go to any slide </button>
    </div>
  );
};

const Card = (props) => (
  <div className="timeline__card">
    <div className="timeline__button-wrapper">
      <button className="button-lg timeline__button">{props.year}</button>
    </div>

    <ul className="timeline__list">
      <li className="timeline__list-item">
        <img src={checkmark} className="timeline__checkmark" />
        <p className="mb-0">{props.item1}</p>
      </li>
      <li className="timeline__list-item">
        <img src={checkmark} className="timeline__checkmark" />
        <p className="mb-0">{props.item2}</p>
      </li>
    </ul>
  </div>
);

function Timeline() {
  return (
    <div className="timeline__bg">
      <Container>
        <Row>
          <Col xs={12} md={6}>
            <h2 className="timeline__heading heading1">Our Timeline</h2>
          </Col>
        </Row>
      </Container>

      <Carousel
        responsive={responsive}
        partialVisible={true}
        renderButtonGroupOutside={true}
        customButtonGroup={<ButtonGroup />}
      // customRightArrow={<CustomRightArrow />}
      >
        <Card
          year='2018'
          item1='Research & Develop'
          item2='Provisional Patents'
        />
        <Card
          year='2019'
          item1='External Reviews'
          item2='Improve Performance'
        />
        <Card
          year='2020'
          item1='Prototype & More Patents'
          item2='Deployment & Validation'
        />
        <Card
          year='2021'
          item1='Launching MoneyClip'
          item2='Launching DeFi Bitcoin Protocols'
        />
        <Card
          year='2022'
          item1='Launching MoneyClip'
          item2='Launching DeFi Bitcoin Protocols'
        />
      </Carousel>;
    </div>
  );
}

export default Timeline;
