import './MailingList.css';
import wfld from '../../../images/logos/wfld.svg';
import gear from '../../../images/logos/gear.svg';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function MailingList() {
  return (
    <div className="mailing__bg">
      <Container>
        <Row>
          <Col xs={12} className="text-center">
            <img src={wfld} />
            <h2 className="mailing__heading heading2">Get investor updates.</h2>

            <form onSubmit={this.handleSubmit} autoComplete="off">
              <input type="hidden" name="form-name" value="contactUs" />
              <input
                className={`form__input-text ${this.state.formErrors.firstName ? 'form__error-input' : ''}`}
                type="text"
                name="firstName"
                value={firstName}
                onChange={this.handleChange}
                onFocus={this.handleFocus}
                onBlur={this.handleBlur}
                ref={this.firstNameInput}
                autoComplete="random"
              />
              {
                this.state.formErrors.firstName ? <p className="form__error-msg">{this.state.formErrors.firstName}</p> : ''
              }
            </form>
            <button className="button-lg mailing__button">Submit</button>
            <img src={gear} />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default MailingList;
