import './Join.css';
import rightBlack from '../../../images/icons/chevron-right-black.png';

import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Card = (props) => (
  <div className={`join__card join__card--${props.type}`}>
    <span className="join__card-number">{props.number}</span>

    <div className="join__button-wrapper">
      <button className={`button-lg join__button--${props.type}`}>{props.cta}</button>
    </div>
  </div>
);

function Join() {
  return (
    <div className="join__bg">
      <Container>
        <Row>
          <Col xs={12} md={6} lg={3}>
            <Card
              number='5'
              cta='PhDs'
              type='left'
            />
          </Col>
          <Col xs={12} md={6} lg={3}>
            <Card
              number='15'
              cta='Engineers'
              type='right'
            />
          </Col>
          <Col xs={12} lg={6}>
            <h2 className="join__heading heading2">We have decades of banking, finance, and consumer-product experience.</h2>
            <Link to='/' className="link join__link">
              <span className="heading5">
                Learn More <img src={rightBlack} style={{ marginLeft: 8 }} alt="Chevron right" />
              </span>
            </Link>
          </Col>

        </Row>
      </Container>
    </div>
  );
}

export default Join;
