import './FAQ.css'
import chevronUp from '../../images/icons/chevron-up.svg';
import chevronDown from '../../images/icons/chevron-down.svg';
import Header from '../Shared/Header/Header';
import ContactBanner from '../Shared/ContactBanner/ContactBanner';


import { Helmet } from 'react-helmet';
import { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';



function FAQ() {
  const [faqs, setFaqs] = useState([]);
  const [selected, setSelected] = useState(null);

  const onClick = (index) => {
    if (index === selected) {
      setSelected(null);
    } else {
      setSelected(index);
    }
  }

  return (
    <>
      <Helmet>
        <title>erthos&trade; - FAQs</title>
        <meta name="description"
          content="Single use plastics made with erthos solutions are 100% compostable, and compatible with existing plastic technology, making plant powered plastics the natural choice." />
        <meta property="og:title" content="erthos&trade; - FAQs" />
        <meta property="og:description" content="Plant powered alternatives to traditional plastics" />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://www.erthos.ca/faq" />
        <meta property="og:image" content="https://www.erthos.ca/leaf-home-mobile.png" />
      </Helmet>

      <Header
        title="FAQ"
        subtitle="Introducing the most sustainable, ocean safe alternative to plastic that lasts for hours, not centuries."
        blob="beige"
      />

      {/* Desktop */}
      <Container className="d-none d-md-block">
        <Row>
          <Col xs={12}>
            <div style={{ maxWidth: 1060, margin: 'auto', marginTop: 265 }}>
              {faqs.map((faq, index) => (
                <div className="faq__qa-container--desktop">
                  <div
                    className={`faq__q-container--desktop ${selected === index ? 'faq__q-container-selected--desktop' : ''}`}
                    onClick={() => onClick(index)}
                  >
                    <h2 className={`header4 mb-0 ${selected === index ? 'primary-green' : ''}`} style={{ maxWidth: 840 }}>
                      {faq.question}
                    </h2>
                    <img src={selected === index ? chevronUp : chevronDown} alt={selected === index ? 'Collapse' : 'Open'} />
                  </div>

                  {selected === index &&
                    <h3 className="header5 primary-green" style={{ marginBottom: 52 }}>{faq.answer}</h3>
                  }
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>

      {/* Mobile */}
      <div className="d-md-none" style={{ marginTop: 120 }}>
        {faqs.map((faq, index) => (
          <div className="faq__qa-container--mobile">
            <div
              className={`faq__q-container--mobile ${selected === index ? 'faq__q-container-selected--mobile' : ''}`}
              onClick={() => onClick(index)}
            >
              <Container>
                <Row>
                  <Col xs={12} className="d-flex justify-content-between">
                    <h2 className={`header4 mb-0 ${selected === index ? 'primary-green' : ''}`} style={{ maxWidth: 259 }}>
                      {faq.question}
                    </h2>
                    <img src={selected === index ? chevronUp : chevronDown} alt={selected === index ? 'Collapse' : 'Open'} />
                  </Col>
                </Row>
              </Container>
            </div>

            {selected === index &&
              <Container>
                <Row>
                  <Col xs={12}>
                    <h3 className="header5 primary-green" style={{ marginBottom: 35 }}>{faq.answer}</h3>
                  </Col>
                </Row>
              </Container>
            }
          </div>
        ))}
      </div>

      <ContactBanner background="beige" />
    </>
  );
}

export default FAQ;
