import './Contact.css'
import instagram from '../../images/social/social-insta-black.svg';
import linkedin from '../../images/social/social-linkedin-black.svg';
import twitter from '../../images/social/social-twitter-black.svg';
import Header from '../Shared/Header/Header';

import { Helmet } from 'react-helmet';
import { Component, createRef } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const encode = (data) => {
  return Object.keys(data)
    .map(key => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
    .join("&");
}

export default class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      industry: '',
      industryOther: '',
      help: '',
      message: '',
      formErrors: {
        firstName: '',
        lastName: '',
        email: '',
        industryOther: '',
        message: '',
      },
      activeLabels: {
        firstName: false,
        lastName: false,
        email: false,
        industry: false,
        industryOther: false,
        help: false,
        message: false,
      },
      isIndustryDropdownOpen: false,
      isHelpDropdownOpen: false,
      isSent: false,
    };
    this.submitBtn = createRef();
    this.firstNameInput = createRef();
    this.lastNameInput = createRef();
    this.emailInput = createRef();
    this.industryInput = createRef();
    this.industryOtherInput = createRef();
    this.helpInput = createRef();
    this.messageInput = createRef();
  }

  handleLabelClick = (input) => {
    // Focuses the respective ref
    this[`${input}Input`].current.focus();
  }

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
    if (name === 'industry' || name === 'help') {
      this.handleSelect(e);
    }
    // Reset the industry free form field error
    if (name === 'industry') {
      const formErrors = this.state.formErrors;
      this.setState({
        formErrors: {
          ...formErrors,
          industryOther: '',
        },
      });
    }
  };

  handleSelect = e => {
    const { name, value } = e.target;
    const activeLabel = !(value === '')
    const activeLabels = this.state.activeLabels;
    this.setState({
      activeLabels: {
        ...activeLabels,
        [name]: activeLabel
      }
    });
  }

  handleFocus = e => {
    const { name } = e.target;
    const formErrors = this.state.formErrors;
    const activeLabels = this.state.activeLabels;
    this.setState({
      formErrors: {
        ...formErrors,
        [name]: '',
      },
      activeLabels: {
        ...activeLabels,
        [name]: true,
      }
    });
  }

  handleBlur = e => {
    const { name, value } = e.target;
    let activeLabel = true;
    switch (name) {
      case 'firstName':
        if (value === '') {
          activeLabel = false;
        }
        break;
      case 'lastName':
        if (value === '') {
          activeLabel = false;
        }
        break;
      case 'email':
        if (value === '') {
          activeLabel = false;
        }
        break;
      case 'industryOther':
        if (value === '') {
          activeLabel = false;
        }
        break;
      case 'message':
        if (value === '') {
          activeLabel = false;
        }
        break;
      default:
        break;
    }
    const activeLabels = this.state.activeLabels;
    this.setState({
      activeLabels: {
        ...activeLabels,
        [name]: activeLabel
      },

    });
  }

  toggleDropdown = (input) => {
    const formErrors = this.state.formErrors;
    this.setState({
      [`is${input === 'help' ? 'Help' : 'Industry'}DropdownOpen`]: !this.state[`is${input === 'help' ? 'Help' : 'Industry'}DropdownOpen`],
      formErrors: {
        ...formErrors,
        [input]: '',
      },
    });
  };

  handleDropdownSelect = (input, value) => {
    const activeLabels = this.state.activeLabels;
    this.setState({
      [`is${input === 'help' ? 'Help' : 'Industry'}DropdownOpen`]: false,
      [input]: value,
      activeLabels: {
        ...activeLabels,
        [input]: true
      }
    });
  }

  handleSubmit = e => {
    const { firstName, lastName, email, industry, industryOther, help } = this.state;
    let firstNameErrorMsg, lastNameErrorMsg, emailErrorMsg, industryErrorMsg, industryOtherErrorMsg, helpErrorMsg = '';
    if (firstName === '') {
      firstNameErrorMsg = 'First name required';
    }
    if (lastName === '') {
      lastNameErrorMsg = 'Last name required';
    }
    if (email === '') {
      emailErrorMsg = 'Email required';
    } else if (!email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)) {
      emailErrorMsg = 'Please enter a valid email';
    }
    if (industry === '') {
      industryErrorMsg = 'Industry required';
    } else if (industry === 'Other' && industryOther === '') {
      industryOtherErrorMsg = 'Industry required';
    }
    if (help === '') {
      helpErrorMsg = 'Reason required';
    }
    const formErrors = this.state.formErrors;
    this.setState({
      formErrors: {
        ...formErrors,
        firstName: firstNameErrorMsg,
        lastName: lastNameErrorMsg,
        email: emailErrorMsg,
        industry: industryErrorMsg,
        industryOther: industryOtherErrorMsg,
        help: helpErrorMsg
      },
    });

    const hasErros = firstNameErrorMsg || lastNameErrorMsg || emailErrorMsg || industryErrorMsg || industryOtherErrorMsg || helpErrorMsg;
    if (!hasErros) {
      fetch("/", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: encode({ "form-name": "contactUs", ...this.state })
      })
        .then(() => this.setState({ isSent: true }))
        .catch(error => alert(error));
    }

    this.submitBtn.current.blur();
    e.preventDefault();
  };

  render() {
    const { firstName, lastName, email, industry, isIndustryDropdownOpen, industryOther,
      help, isHelpDropdownOpen, message, isSent } = this.state;
    return (
      <>
        <Helmet>
          <title>erthos&trade; - Contact us</title>
          <meta name="description"
            content="Single use plastics made with erthos solutions are 100% compostable, and compatible with existing plastic technology, making plant powered plastics the natural choice." />
          <meta property="og:title" content="erthos&trade; - Contact us" />
          <meta property="og:description" content="Plant powered alternatives to traditional plastics" />
          <meta property="og:type" content="website" />
          <meta property="og:url" content="https://www.erthos.ca/contact" />
          <meta property="og:image" content="https://www.erthos.ca/leaf-home-mobile.png" />
        </Helmet>

        <Header
          title="Get in touch"
          blob="black"
        />

        <Container className="contact__container">
          <Row>
            {/* Info Column */}
            <Col xs={12} md={6} >
              <p className="header4 contact__header">Some want a planet without plastics. We want better plastics. Do you?</p>

              <h2 className="header4 contact__offices">Our Offices</h2>
              <Row>
                <Col xs={6} >
                  <label className="eyebrow">CANADA HQ</label>
                  <p className="header5 primary-green mb-0">Unit 5, 2735 Matheson Blvd. E<br></br>Mississauga, ON</p>
                </Col>
                <Col xs={6} >
                  <label className="eyebrow">CHINA</label>
                  <p className="header5 primary-green mb-0">Wancheng Comprehensive Warehouse, No. 6, Futian District, Shenzhen </p>
                </Col>
              </Row>

              <h2 className="header4 contact__follow-us">Follow Us</h2>
              <div className="d-flex justify-content-between" style={{ maxWidth: 190, marginBottom: 60 }}>
                <a href="https://twitter.com/planeterthos/">
                  <img src={twitter} alt="Twitter logo" />
                </a>
                <a href="https://www.linkedin.com/company/erthosinc">
                  <img src={linkedin} alt="Linkedin logo" />
                </a>
                <a href="https://www.instagram.com/planeterthos/">
                  <img src={instagram} alt="Instagram logo" />
                </a>
              </div>
            </Col>

            {/* Form Column */}
            <Col xs={12} md={6} style={{ marginBottom: 300 }}>
              <h2 className="header4" style={{ marginBottom: 3 }}>Contact Us</h2>
              <form onSubmit={this.handleSubmit} autoComplete="off">
                <input type="hidden" name="form-name" value="contactUs" />
                <Row>
                  <Col xs={12} sm={6}>
                    <label className={`form__label ${this.state.activeLabels.firstName ? 'form__label--active' : ''}`}
                      onClick={() => this.handleLabelClick('firstName')}
                    >First Name</label>
                    <input
                      className={`form__input-text ${this.state.formErrors.firstName ? 'form__error-input' : ''}`}
                      type="text"
                      name="firstName"
                      value={firstName}
                      onChange={this.handleChange}
                      onFocus={this.handleFocus}
                      onBlur={this.handleBlur}
                      ref={this.firstNameInput}
                      autoComplete="random"
                    />
                    {
                      this.state.formErrors.firstName ? <p className="form__error-msg">{this.state.formErrors.firstName}</p> : ''
                    }
                  </Col>
                  <Col xs={12} sm={6}>
                    <label className={`form__label ${this.state.activeLabels.lastName ? 'form__label--active' : ''}`}
                      onClick={() => this.handleLabelClick('lastName')}
                    >Last Name</label>
                    <input
                      className={`form__input-text ${this.state.formErrors.lastName ? 'form__error-input' : ''}`}
                      type="text"
                      name="lastName"
                      value={lastName}
                      onChange={this.handleChange}
                      onFocus={this.handleFocus}
                      onBlur={this.handleBlur}
                      ref={this.lastNameInput}
                      autoComplete="random"
                    />
                    {
                      this.state.formErrors.lastName ? <p className="form__error-msg">{this.state.formErrors.lastName}</p> : ''
                    }
                  </Col>
                </Row>
                <Row>
                  <Col xs={12}>
                    <label className={`form__label ${this.state.activeLabels.email ? 'form__label--active' : ''}`}
                      onClick={() => this.handleLabelClick('email')}
                    >Email</label>
                    <input
                      className={`form__input-text ${this.state.formErrors.email ? 'form__error-input' : ''}`}
                      type="email"
                      name="email"
                      value={email}
                      onChange={this.handleChange}
                      onFocus={this.handleFocus}
                      onBlur={this.handleBlur}
                      ref={this.emailInput}
                      autoComplete="random"
                    />
                    {
                      this.state.formErrors.email ? <p className="form__error-msg">{this.state.formErrors.email}</p> : ''
                    }
                  </Col>
                </Row>
                <Row>
                  <Col xs={12}>
                    <div className="d-md-none">
                      <label className={`form__label ${this.state.activeLabels.industry ? 'form__label--active' : ''}`}>What industry are you in?</label>
                      <select className={`form__dropdown-main ${industry ? 'form__full-opacity' : 'form__zero-opacity'}
                        ${this.state.formErrors.industry ? 'form__error-input' : ''}`}
                        name="industry" value={industry} onChange={this.handleChange} ref={this.industryInput} >
                        <option value="" >What industry are you in?</option>
                        <option value="Agricultural Supplier">Agricultural Supplier</option>
                        <option value="Manufacturer">Manufacturer</option>
                        <option value="Consumer Brand">Consumer Brand</option>
                        <option value="Enviro-agency">Enviro-agency</option>
                        <option value="Other">Other</option>
                      </select>
                    </div>
                    <div className="d-none d-md-block">
                      <label className={`form__label ${this.state.activeLabels.industry ? 'form__label--active' : ''}`}>What industry are you in?</label>
                      <div style={{ position: 'relative' }}>
                        <p className={`form__dropdown-main
                          ${isIndustryDropdownOpen ? 'form__dropdown-open' : ''}
                          ${industry ? 'form__full-opacity' : ''}
                          ${this.state.formErrors.industry ? 'form__error-input' : ''}
                          `}
                          onClick={() => this.toggleDropdown('industry')}>{industry ? industry : 'What industry are you in?'}</p>
                        {
                          isIndustryDropdownOpen ?
                            <div className="form__dropdown-options">
                              <p className={`form__dropdown-option ${industry === 'Agricultural Supplier' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('industry', 'Agricultural Supplier')}>Agricultural Supplier</p>
                              <p className={`form__dropdown-option ${industry === 'Manufacturer' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('industry', 'Manufacturer')}>Manufacturer</p>
                              <p className={`form__dropdown-option ${industry === 'Consumer Brand' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('industry', 'Consumer Brand')}>Consumer Brand</p>
                              <p className={`form__dropdown-option ${industry === 'Enviro-agency' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('industry', 'Enviro-agency')}>Enviro-agency</p>
                              <p className={`form__dropdown-option ${industry === 'Other' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('industry', 'Other')}>Other</p>
                            </div>
                            : ''
                        }
                      </div>
                    </div>
                    {
                      this.state.formErrors.industry ? <p className="form__error-msg">{this.state.formErrors.industry}</p> : ''
                    }
                  </Col>
                </Row>
                {
                  this.state.industry === 'Other' &&
                  <Row>
                    <Col xs={12}>
                      <label className={`form__label ${this.state.activeLabels.industryOther ? 'form__label--active' : ''}`}
                        onClick={() => this.handleLabelClick('industryOther')}
                      >Industry</label>
                      <input
                        className={`form__input-text ${this.state.formErrors.industryOther ? 'form__error-input' : ''}`}
                        type="text"
                        name="industryOther"
                        value={industryOther}
                        onChange={this.handleChange}
                        onFocus={this.handleFocus}
                        onBlur={this.handleBlur}
                        ref={this.industryOtherInput}
                        autoComplete="random"
                      />
                      {
                        this.state.formErrors.industryOther ? <p className="form__error-msg">{this.state.formErrors.industryOther}</p> : ''
                      }
                    </Col>
                  </Row>
                }
                <Row>
                  <Col xs={12}>
                    <div className="d-md-none">
                      <label className={`form__label ${this.state.activeLabels.help ? 'form__label--active' : ''}`}>How can we help?</label>
                      <select className={`form__dropdown-main ${help ? 'form__full-opacity' : 'form__zero-opacity'}
                        ${this.state.formErrors.help ? 'form__error-input' : ''}`}
                        name="help" value={help} onChange={this.handleChange} ref={this.helpInput}>
                        <option value="">How can we help?</option>
                        <option value="Place an order">Place an order</option>
                        <option value="Plant purpose">Plant purpose</option>
                        <option value="Product collaboration">Product collaboration</option>
                        <option value="Partnerships">Partnerships</option>
                      </select>
                    </div>
                    <div className="d-none d-md-block">
                      <label className={`form__label ${this.state.activeLabels.help ? 'form__label--active' : ''}`}>How can we help?</label>
                      <div style={{ position: 'relative' }}>
                        <p className={`form__dropdown-main
                          ${isHelpDropdownOpen ? 'form__dropdown-open' : ''}
                          ${help ? 'form__full-opacity' : ''}
                          ${this.state.formErrors.help ? 'form__error-input' : ''}`}
                          onClick={() => this.toggleDropdown('help')}>{help ? help : 'How can we help?'}</p>
                        {
                          isHelpDropdownOpen ?
                            <div className="form__dropdown-options">
                              <p className={`form__dropdown-option ${help === 'Place an order' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('help', 'Place an order')}>Place an order</p>
                              <p className={`form__dropdown-option ${help === 'Plant purpose' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('help', 'Plant purpose')}>Plant purpose</p>
                              <p className={`form__dropdown-option ${help === 'Product collaboration' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('help', 'Product collaboration')}>Product collaboration</p>
                              <p className={`form__dropdown-option ${help === 'Partnerships' ? 'd-none' : ''}`}
                                onClick={() => this.handleDropdownSelect('help', 'Partnerships')}>Partnerships</p>
                            </div>
                            : ''
                        }
                      </div>
                    </div>
                    {
                      this.state.formErrors.help ? <p className="form__error-msg">{this.state.formErrors.help}</p> : ''
                    }
                  </Col>
                </Row>

                <Row>
                  <Col xs={12}>
                    <label className={`form__label ${this.state.activeLabels.message ? 'form__label--active' : ''}`}
                      onClick={() => this.handleLabelClick('message')}
                    >Message</label>
                    <textarea
                      className="form__input-text form__input-textarea"
                      name="message"
                      value={message}
                      onChange={this.handleChange}
                      onFocus={this.handleFocus}
                      onBlur={this.handleBlur}
                      ref={this.messageInput}
                    ></textarea>
                  </Col>
                </Row>

                <Row>
                  <Col xs={12}>
                    <button className={`button-secondary form__btn ${isSent ? 'form__btn-submitted' : ''}`}
                      ref={this.submitBtn} type="submit" disabled={isSent}>{isSent ? 'Sent!' : 'Send'}</button>
                  </Col>
                </Row>
              </form>
            </Col>
          </Row>
        </Container>
      </>
    )
  }
}
