import './About.css';
import chang from '../../images/people/people-chang.jpg';
import kritika from '../../images/people/people-kritika.jpg';
import nuha from '../../images/people/people-nuha.jpg';
// import leafDesktop from '../../images/backgrounds/leaf-about-desktop.jpg';
// import leafMobile from '../../images/backgrounds/leaf-about-mobile.jpg';
// import timelineDesktop from '../../images/backgrounds/timeline-desktop.svg';
// import timelineMobile from '../../images/backgrounds/timeline-mobile.svg';
import beige from '../../images/blobs/blob-xl-beige-about.svg';
import rust from '../../images/blobs/blob-rust.svg';
import sustainabilityDesktop from '../../images/cycles/cycle-sustainability-desktop.svg';
import sustainabilityMobile from '../../images/cycles/cycle-sustainability-mobile.svg';
import Header from '../Shared/Header/Header';

import { Helmet } from 'react-helmet';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const BeliefsBodyDesktop = () => (
  <>
    {/* <img src={leafDesktop} className="about__beliefs-image--desktop" alt="Plant leaves" /> */}
    <div>
      <img src={beige} className="about__beliefs-blob" alt="Beige circle" />
      <label className="eyebrow primary-rust d-block mt-4">AT OUR CORE</label>
      <h2 className="header2" style={{ marginBottom: 40 }}>We believe in</h2>

      <h3 className="header4">Earth</h3>
      <p className="header5 about__beliefs-point">
        We believe in the power of plants<br></br>
        We care about our impact on our planet<br></br>
        We support those who protect it
      </p>
      <h3 className="header4">Community</h3>
      <p className="header5 about__beliefs-point">
        We started by listening and are dedicated to growing<br></br>
        We collaborate to deliver<br></br>
        We believe we all have a part in this transformation
      </p>
      <h3 className="header4">Innovation</h3>
      <p className="header5 about__beliefs-point mb-0">
        We are visionaries of change that exceed today’s standards<br></br>
        We are environmentalists harnessing science<br></br>
        We are devoted to plant chemistry, biology and engineering
      </p>
    </div>
  </>
);

const BeliefsBodyMobile = () => (
  <>
    <Container>
      <Row>
        <Col xs={12}>
          <label className="eyebrow primary-rust d-block" style={{ marginTop: 120 }}>AT OUR CORE</label>
          <h2 className="header2 mb-0">We believe in</h2>
        </Col>
      </Row>
    </Container>

    {/* <img src={leafMobile} className="about__beliefs-image--mobile" alt="Plant leaves" /> */}

    <Container>
      <Row>
        <Col xs={12}>
          <h3 className="header4">Earth</h3>
          <p className="header5 about__beliefs-point">
            We believe in the power of plants<br></br>
            We care about our impact on our planet<br></br>
            We support those who protect it
          </p>
          <h3 className="header4">Community</h3>
          <p className="header5 about__beliefs-point">
            We started by listening and are dedicated to growing<br></br>
            We collaborate to deliver<br></br>
            We believe we all have a part in this transformation
          </p>
          <h3 className="header4">Innovation</h3>
          <p className="header5 about__beliefs-point mb-0">
            We are visionaries of change that exceed today’s standards<br></br>
            We are environmentalists harnessing science<br></br>
            We are devoted to plant chemistry, biology and engineering
          </p>
        </Col>
      </Row>
    </Container>
  </>

);


function About() {
  const [metrics, setMetrics] = useState({});


  return (
    <>
      <Helmet>
        <title>erthos&trade; - About us</title>
        <meta name="description"
          content="Single use plastics made with erthos solutions are 100% compostable, and compatible with existing plastic technology, making plant powered plastics the natural choice." />
        <meta property="og:title" content="erthos&trade; - About us" />
        <meta property="og:description" content="Plant powered alternatives to traditional plastics" />
        <meta property="og:type" content="website" />
        <meta property="og:url" content="https://www.erthos.ca/about" />
        <meta property="og:image" content="https://www.erthos.ca/leaf-home-mobile.png" />
      </Helmet>

      <Container>
        <Row>
          <Col>
            <h2>Contact us</h2>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default About;
